import React from 'react'
import { useState, useEffect } from 'react';
import './LoginForm.css'


const LoginForm = () => {


    const initialValues = { username: "", email: "", password: "" };
    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormValues({ ...formValues, [name]: value });
    }



    const handleSubmit = (event) => {
        event.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
    };

    useEffect(() => {

        if (Object.keys(formErrors).length === 0 && isSubmit) {
            handleLogin(formValues);
        }
    }, [formErrors]);


    const handleLogin = (data) => {

    }


    const validate = (values) => {
        const errors = {};

        if (!values.username) {
            errors.username = "Username is required!";
        }

        if (!values.password) {
            errors.password = "Password is required";
        }
        return errors;
    };



    return (

        <><h1 className="events-dashboard">Events Dashboard</h1>
            <div className="login-form">
                <img className="logos-icon" alt="" id="logo" src="../SNTC Logo@2x.png" />


                <form className="group-form" onSubmit={handleSubmit}>


                    <div className='rectangle-div'>
                        <div className='field'>
                            <label className='userName-label'>User Name</label>
                            <input className="rectangle-input" type="text" name="username" placeholder='Username' value={formValues.username} onChange={handleChange} />
                            <p>{formErrors.username}</p>
                        </div>

                        <div className='field'>
                            <label className='password-label'>Password</label>
                            <input className="rectangle-input1" type="password" name="password" placeholder='Password' value={formValues.password} onChange={handleChange} />
                            <p>{formErrors.password}</p>
                        </div>
                        <div>
                            <button className='login-button' type='submit'>Login</button>
                        </div>

                    </div>

                </form>
            </div>
        </>
    )
}

export default LoginForm
